function AddressListDataSource(){var t=new kendo.data.DataSource({transport:{read:{url:"/api/addresses",dataType:"json"}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},address:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return t}function AddressListGrid(){jQuery("#addressList").kendoGrid({dataSource:AddressListDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"address1",title:"Address1"},{command:[{name:"edit",text:"Edit",click:edit_systems},{name:"Delete",text:"Delete",click:delete_systems}]}]})}function edit_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));openModal("address/"+e.id+"/edit","#addressModal")}function delete_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));doDelete("/api/addresses/"+e.id,e.id,"#addressList")}$(document).ready(function(){AddressListGrid()});