function subInfoDataSource(){var t=new kendo.data.DataSource({transport:{read:{url:"/api/subjects",dataType:"json",data:{class_list_id:class_list_id}}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},name:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return t}function subInfoGrid(){jQuery("#subInfo").kendoGrid({dataSource:subInfoDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"name",title:"Subject"},{command:[{name:"edit",text:"Edit",click:edit_systems},{name:"Delete",text:"Delete",click:delete_systems}]}]})}function examTypeDataSource(){var t=new kendo.data.DataSource({transport:{read:{url:"/api/exam_types",dataType:"json",data:{class_list_id:class_list_id}}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},name:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return t}function examTypeGrid(){jQuery("#examInfo").kendoGrid({dataSource:examTypeDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"name",title:"Subject"},{command:[{name:"edit",text:"Edit",click:edit_systems},{name:"Delete",text:"Delete",click:delete_systems}]}]})}function studentDataSource(){var t=new kendo.data.DataSource({transport:{read:{url:"/api/students",dataType:"json",data:{class_list_id:class_list_id}}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},name:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return t}function studentGrid(){jQuery("#studentInfo").kendoGrid({dataSource:studentDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"first_name",title:"First Name"},{field:"last_name",title:"Last Name"},{command:[{name:"edit",text:"Edit",click:edit_systems},{name:"Delete",text:"Delete",click:delete_systems},{name:"show",text:"Exam Info",click:showExamInfo}]}]})}function staffDataSource(){var t=new kendo.data.DataSource({transport:{read:{url:"/api/staffs",dataType:"json",data:{class_list_id:class_list_id}}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},name:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return t}function staffGrid(){jQuery("#staffInfo").kendoGrid({dataSource:staffDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"first_name",title:"First Name"},{field:"last_name",title:"Last Name"},{field:"subject",title:"Subject"}]})}function edit_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));openModal("address/"+e.id+"/edit","#classListModal")}function delete_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));doDelete("/api/addresses/"+e.id,e.id,"#classListModal")}function showClass(t){window.location.href="/class_lists/class_list_info"}function showExamInfo(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));window.location.href="/exam_infos/"+e.id+"/edit"}$(document).ready(function(){$("#tabstrip").kendoTabStrip().data("kendoTabStrip");subInfoGrid(),examTypeGrid(),studentGrid(),staffGrid()});