function classListDataSource(){var t=new kendo.data.DataSource({transport:{read:{url:"/api/class_lists",dataType:"json"}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},board_name:{type:"string",editable:!0},class_name:{type:"string",editable:!0},section:{type:"string",editable:!0},major:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return t}function classListGrid(){jQuery("#classList").kendoGrid({dataSource:classListDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"class_name",title:"Class"},{field:"section",title:"Section"},{field:"major",title:"Major"},{command:[{name:"edit",text:"Edit",click:edit_systems},{name:"Delete",text:"Delete",click:delete_systems},{name:"show",text:"Show",click:showClass}]}]})}function edit_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));openModal("address/"+e.id+"/edit","#classListModal")}function delete_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));doDelete("/api/addresses/"+e.id,e.id,"#classListModal")}function showClass(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));window.location.href="class_lists/"+e.id+"/class_list_info"}$(document).ready(function(){classListGrid()});