function callExamTypesGrid(){$.each(exam_types,function(t,e){examDataSource()})}function examDataSource(t){var e=new kendo.data.DataSource({transport:{read:{url:"/api/marks",dataType:"json",data:{class_list_id:t}}},schema:{total:"total",data:"data",model:{id:"id",fields:createSubHash}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return e}function examGrid(t,e){jQuery(t).kendoGrid({dataSource:examDataSource(e),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"first_name",title:"First Name"},{field:"last_name",title:"Last Name"},{command:[{name:"edit",text:"Edit",click:edit_systems},{name:"Delete",text:"Delete",click:delete_systems},{name:"show",text:"Exam Info",click:showExamInfo}]}]})}function createSubHash(){return{id:{type:"string",editable:!1},subject:{type:"string",editable:!0}}}$(document).ready(function(){$("#tabstrip").kendoTabStrip().data("kendoTabStrip")});