function FeesStructureListDataSource(){var e=new kendo.data.DataSource({transport:{read:{url:"/api/fees_structures",dataType:"json"}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},board_name:{type:"string",editable:!0},class_name:{type:"string",editable:!0},fees_type:{type:"string",editable:!0},amount:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return e}function FeesStructureListGrid(){jQuery("#fees_structure_List").kendoGrid({dataSource:FeesStructureListDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"class_list",title:"Class"},{field:"fees_type",title:"Fee Type"},{field:"amount",title:"Amount"},{command:[{name:"edit",text:"Edit",click:edit_systems},{name:"Delete",text:"Delete",click:delete_systems}]}]})}function edit_systems(e){var t=this.dataItem(jQuery(e.currentTarget).closest("tr"));openModal("fees_structures/"+t.id+"/edit","#feesstructureModal")}function delete_systems(e){var t=this.dataItem(jQuery(e.currentTarget).closest("tr"));doDelete("/api/fees_structures/"+t.id,t.id,"#fees_structure_List")}$(document).ready(function(){FeesStructureListGrid()});