function SalaryDetailListDataSource(){var t=new kendo.data.DataSource({transport:{read:{url:"/api/salary_details",dataType:"json"}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},board_name:{type:"string",editable:!0},staff_name:{type:"string",editable:!0},fees_type:{type:"string",editable:!0},amount:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return t}function SalaryDetailListGrid(){jQuery("#salary_detail_List").kendoGrid({dataSource:SalaryDetailListDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"staff_name",title:"Staff Name"},{field:"fees_type",title:"Fee Type"},{field:"amount",title:"Amount"},{command:[{name:"edit",text:"Edit",click:edit_systems},{name:"Delete",text:"Delete",click:delete_systems}]}]})}function edit_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));openModal("salary_details/"+e.id+"/edit","#salarydetailModal")}function delete_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));doDelete("/api/salary_details/"+e.id,e.id,"#salary_detail_List")}$(document).ready(function(){SalaryDetailListGrid()});