function userListDataSource(){var t=new kendo.data.DataSource({transport:{read:{url:"/site_customizations",dataType:"json",complete:function(t,e){}},update:{url:function(t,e){return"/site_customizations/"+t.models[0].id},dataType:"json",type:"PUT",complete:function(t,e){jQuery("#userList").data("kendoGrid").dataSource.read()}},parameterMap:function(t,e){return"update"==e&&t.models?{user:t.models[0]}:void 0}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},menu_name:{type:"string",editable:!0},bg_color:{type:"string",editable:!0},text_color:{type:"string",editable:!0},font_style:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return t}function userListGrid(){jQuery("#userList").kendoGrid({dataSource:userListDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"menu_name",title:"Menu Name"},{field:"bg_color",title:"Background Color"},{field:"text_color",title:"Text Color"},{field:"font_style",title:"Font Family"},{command:[{name:"edit",text:" "},{name:"Delete",text:" ",click:delete_systems}],width:"150px"}]})}function edit_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));openModal("/site_customizations/"+e.id+"/edit","#userModal")}function delete_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));doDelete(e.id)}$(document).ready(function(){userListGrid()});