function markListDataSource(){var t=new kendo.data.DataSource({transport:{read:{url:"/api/marks",dataType:"json"},parameterMap:function(t,e){return"read"==e?{class_list_id:$("#class_list option:selected").val(),exam_type_id:$("#exam_types option:selected").val()}:void 0}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},first_name:{type:"string",editable:!0},last_name:{type:"string",editable:!0},student_id:{type:"string",editable:!0},exam_type:{type:"string",editable:!0},mark:{type:"string",editable:!0}}}},batch:!0,pageSize:10,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return t}function markListGrid(){$("#markListGrid").kendoGrid({dataSource:markListDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"first_name",title:"First Name"},{field:"last_name",title:"Last Name"},{field:"exam_type",title:"Exam Type"},{field:"mark",title:"Mark"},{command:[{name:"edit",text:"Edit",click:edit_systems},{name:"Delete",text:"Delete",click:delete_systems}]}]})}function edit_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));openModal("/staffs/"+e.id+"/edit","#staffModal")}function delete_systems(t){var e=this.dataItem(jQuery(t.currentTarget).closest("tr"));doDelete("/api/marks/"+e.id,e.id,"#markListGrid")}function changeMarkList(){refereshGrid("#markListGrid")}$(document).ready(function(){markListGrid()});