function staffDataSource(){var e=new kendo.data.DataSource({transport:{read:{url:"/api/staffs",dataType:"json"}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},name:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return e}function staffGrid(){jQuery("#staffInfo").kendoGrid({dataSource:staffDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"first_name",title:"First Name"},{field:"last_name",title:"Last Name"},{field:"subject",title:"Subject"}]})}$(document).ready(function(){staffGrid()});