function studentDataSource(){var e=new kendo.data.DataSource({transport:{read:{url:"/api/students",dataType:"json"}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},name:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return e}function studentGrid(){jQuery("#studentInfo").kendoGrid({dataSource:studentDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"first_name",title:"First Name"},{field:"last_name",title:"Last Name"}]})}$(document).ready(function(){studentGrid()});