function userListDataSource(){var e=new kendo.data.DataSource({transport:{read:{url:"/users",dataType:"json",complete:function(e,t){}},update:{url:function(e,t){return"/users/"+e.models[0].id},dataType:"json",type:"PUT",complete:function(e,t){jQuery("#userList").data("kendoGrid").dataSource.read()}},parameterMap:function(e,t){return"update"==t&&e.models?{user:e.models[0]}:void 0}},schema:{total:"total",data:"data",model:{id:"id",fields:{id:{type:"string",editable:!1},username:{type:"string",editable:!0},email_id:{type:"string",editable:!0},role:{type:"string",editable:!0}}}},batch:!0,pageSize:100,serverPaging:!1,serverFiltering:!1,serverSorting:!1});return e}function userListGrid(){jQuery("#userList").kendoGrid({dataSource:userListDataSource(),resizable:!0,pageable:{refresh:!0},scrollable:!1,sortable:!0,columns:[{field:"username",title:"User Name"},{field:"email",title:"Email"},{field:"role_id",title:"role"},{command:[{name:"edit",text:" "},{name:"Delete",text:" ",click:delete_systems}],width:"150px"}]})}function edit_systems(e){var t=this.dataItem(jQuery(e.currentTarget).closest("tr"));openModal("/users/"+t.id+"/edit","#userModal")}function delete_systems(e){var t=this.dataItem(jQuery(e.currentTarget).closest("tr"));doDelete(t.id)}$(document).ready(function(){userListGrid()});